// package Quarantine; import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.util.HashMap;import java.util.HashSet;import java.util.StringTokenizer; public class MarCinAndTrainCamp {    public static void main(String[] args)throws IOException {        BufferedReader br=new BufferedReader(new InputStreamReader(System.in));        int n=Integer.parseInt(br.readLine());        long a[]=new long[n];        int b[]=new int[n];        StringTokenizer st=new StringTokenizer(br.readLine());        HashMap<Long,Integer> count=new HashMap<>();        for(int i=0;i<n;i++){            a[i]=Long.parseLong(st.nextToken());            count.put(a[i],count.getOrDefault(a[i],0)+1);        }        st=new StringTokenizer(br.readLine());        for(int i=0;i<n;i++){            b[i]=Integer.parseInt(st.nextToken());        }        long ans=0;        HashSet<Long> set=new HashSet<>();        for(int i=0;i<n;i++){            int c=count.get(a[i]);            if(c>1){                ans+=b[i];                set.add(a[i]);            }        }        for(int i=0;i<n;i++){            if(count.get(a[i])==1){//                System.out.println(a[i]);                for(Long num:set){                    if(compare(num,a[i])){                        ans+=b[i];                        break;                    }                }            }        }        System.out.println(ans);    }    public static boolean compare(long a,long b){        for(int i=59;i>=0;i--){            long t=1l<<i;            if((t&a)==0&&(t&b)!=0){                return false;            }        }        return true;    }}
