import java.util.*;public class MyClass {     public static void main(String args[]) {      Scanner sc=new Scanner(System.in);      int t=sc.nextInt();      while(t-->0)      {           int n=sc.nextInt();          int[] arr=new int[n];          int c=0;          for(int i=0;i<n;i++)          arr[i]=sc.nextInt();          int min=arr[n-1];           for(int i=n-1;i>=0;i--)           {               if(arr[i]>min)               {                   c++;               }               min=Math.min(arr[i],min);           }          System.out.println(c);      }    }}
