import java.util.Scanner;public class Main {    public static void main(String args[]) {        Scanner s = new Scanner(System.in);        int t = s.nextInt();        while(t-- != 0){            int n = s.nextInt();            long arr[] = new long[n];            for(int i = 0; i < n; i++){                arr[i] = s.nextLong();            }            long suffix[] = new long[n];            suffix[n - 1] = arr[n - 1];            int count = 0;            for(int i = n - 2; i >= 0; i--){                suffix[i] = Math.min(suffix[i + 1],arr[i]);                if(arr[i] > suffix[i]){                    count++;                }            }            System.out.println(count);        }    }}
