import java.util.Scanner;public class Main{	static Scanner in = new Scanner(System.in);	public static void main(String[] args) {		int n , d ,e;		int ans = Integer.MAX_VALUE ;		n = in.nextInt();d = in.nextInt() ; e = in.nextInt();                int num;	        for(int i = 0;i <= (n / (e * 5));i++) {	        num = (n - e*5 * i) % d;	        ans = Math.min(ans, num);	    }		System.out.println(ans);	}}  			     		  		 	 	 	  	 	
