import java.util.*;public class Solution{    public static void main(String[] args){        Scanner s=new Scanner(System.in);        int boys=s.nextInt();        int girls=s.nextInt();        int n=s.nextInt();        int count=0,count1=0,found=0;int j=n;        if(n%2==0 && boys==n/2 && girls==n/2)                found=1;                        else{            for(int i=0;i<=n;i++){                if(i<=boys && j<=girls)                    count++;                j--;                            }        }        int min=0;        if(found==1)            min=1;        else            min=count;        System.out.println(min);    }}
