import java.util.*;public class file{public static void main(String[] args){Scanner scan=new Scanner(System.in);int b=scan.nextInt();int g=scan.nextInt();int n=scan.nextInt();if(n > (b+g))System.out.println("0");else if(n== b+g)System.out.println("1");else if(n<=b && n<=g){System.out.println(n+1);}else if(n>b && n>g){if(b>g){System.out.println(g-(n-b)+1);}else{System.out.println(b-(n-g)+1);}}else if(g<=b&& g<=n)System.out.println(g+1);else if(b<=g&&b<=n)System.out.println(b+1);}}
