import java.util.*;public class MyClass {     public static void main(String args[]) {      Scanner sc=new Scanner(System.in);      ArrayList<Integer> a=new ArrayList<Integer>();          int n=sc.nextInt();          int[] arr=new int[n];          int p=0,s=0,c=0;          for(int i=0;i<n;i++)          arr[i]=sc.nextInt();          int[] e=arr.clone();         Arrays.sort(arr);         int start=0,end=n-1;         while(start<=end)         {             int temp=arr[start];             arr[start]=arr[end];             arr[end]=temp;             start++;             end--;         }                  while(p<n){          for(int i=0;i<n;i++)          {              int k=arr[p];             if(k==e[i])              {  e[i]=-1;                  a.add(i+1);                  p++;                  if(i==0)                  s=s+1;                  else                  s=s+(arr[i]*i)+1;              break;                          }          }          }                  System.out.println(s);          for(int i:a)          System.out.print(i+" ");    }}
