//package practice; import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.math.BigInteger;import java.util.*; public class Main {	 static class FastReader  	    { 	        BufferedReader br; 	        StringTokenizer st; 	  	        public FastReader() 	        { 	            br = new BufferedReader(new	                     InputStreamReader(System.in)); 	        } 	  	        String next() 	        { 	            while (st == null || !st.hasMoreElements()) 	            { 	                try	                { 	                    st = new StringTokenizer(br.readLine()); 	                } 	                catch (IOException  e) 	                { 	                    e.printStackTrace(); 	                } 	            } 	            return st.nextToken(); 	        } 	  	        int nextInt() 	        { 	            return Integer.parseInt(next()); 	        } 	  	        long nextLong() 	        { 	            return Long.parseLong(next()); 	        } 	  	        double nextDouble() 	        { 	            return Double.parseDouble(next()); 	        } 	  	        String nextLine() 	        { 	            String str = ""; 	            try	            { 	                str = br.readLine(); 	            } 	            catch (IOException e) 	            { 	                e.printStackTrace(); 	            } 	            return str; 	        }	    }	 static void ruffleSort(int[] a) {			//ruffle			int n=a.length;			Random r=new Random();			for (int i=0; i<a.length; i++) {				int oi=r.nextInt(n), temp=a[i];				a[i]=a[oi];				a[oi]=temp;			}						//then sort			Arrays.sort(a);		}	 static boolean prime[] = new boolean[1000001]; 	 	 static void sieveOfEratosthenes() 	    { 	       int n=1000000;	       Arrays.fill(prime, true);	        prime[0]=false;prime[1]=false;	        for(int p = 2; p*p <=n; p++) 	        { 	            // If prime[p] is not changed, then it is a prime 	            if(prime[p] == true) 	            { 	                // Update all multiples of p 	                for(int i = p*p; i <= n; i += p) 	                    prime[i] = false; 	            } 	        } 	      	    } 		public static void main(String[] args) throws IOException  {		FastReader sc=new FastReader();		String s=sc.nextLine();		int count4=0;int count7=0;		for(int i=0;i<s.length();i++) {			if(s.charAt(i)=='4') {				count4++;			}			else if(s.charAt(i)=='7') {				count7++;			}		}		if(count4==0 && count7==0) {			System.out.println("-1");		}		else {			if(count4>=count7) {				System.out.println("4");			}			else {				System.out.println("7");			}		}	}}    
