import java.util.*; public class Main{       public static void main(String[] args) {		Scanner scan = new Scanner(System.in);		int q = scan.nextInt();		while(q>0){		    int n = scan.nextInt();		    q--;		    		    int a[] = new int[n];		    for(int i=0; i<n; i++){		        a[i] = scan.nextInt();		    }		    		    int sum = 0;		    for(int i=0; i<n; i++){		        sum += a[i];		    }		    		    if(sum%n==0){		        System.out.println(sum/n);		    }		    else{		        System.out.println(sum/n +1);		    }		}	}}
