  import java.util.*;import java.io.*;public class A{    public static FastReader sc=new FastReader();        public static PrintWriter  out = new PrintWriter(System.out);         public static void taskSolver(){          int n=sc.nextInt();       long sum=0;Integer arr[]=new Integer[n];       for(int i=0;i<n;i++){           arr[i]=sc.nextInt();sum+=arr[i];       }        out.println((sum+n-1)/n);    }          public static void main(String args[]) throws java.lang.Exception{              int t=sc.nextInt();            while(t-->0)           taskSolver();           out.close();        }           static  class FastReader     {         BufferedReader br;         StringTokenizer st;           public FastReader()         {             br = new BufferedReader(new                     InputStreamReader(System.in));         }         String next()         {             while (st == null || !st.hasMoreElements())             {                 try                {                     st = new StringTokenizer(br.readLine());                 }                 catch (IOException  e)                 {                     e.printStackTrace();                 }             }             return st.nextToken();         }         int nextInt()         {             return Integer.parseInt(next());         }           long nextLong()         {             return Long.parseLong(next());         }           double nextDouble()         {             return Double.parseDouble(next());         }         String nextLine()         {             String str = "";             try            {                 str = br.readLine();             }             catch (IOException e)             {                 e.printStackTrace();             }             return str;         }     }      } 
