import java.util.*;import java.util.Arrays;public class Main{    public static void main(String args[])    {        Scanner sc=new Scanner(System.in);        int t=sc.nextInt();        for(int i=0;i<t;i++)        {            int a=sc.nextInt();            int b=sc.nextInt();            int c=sc.nextInt();            int count=0;            if(b==0)            {                System.out.println(0);            }            else            {                if(a==0)                {                    while(b!=0 && c>1)                    {                        count=count+3;                        b=b-1;                        c=c-2;                    }                    System.out.println(count);                }                else if(c==0)                {                    while(a!=0 && b>1)                    {                        count=count+3;                        a=a-1;                        b=b-2;                    }                    System.out.println(count);                }                else                {                                       while(b!=0 && c>1)                    {                        count=count+3;                        b=b-1;                        c=c-2;                    }                    while(a!=0 && b>1)                    {                        count=count+3;                        a=a-1;                        b=b-2;                    }                    System.out.println(count);                }            }        }    }}
