import java.util.*;public class Solution{    public static void main(String[] args){        Scanner s=new Scanner(System.in);        int t=s.nextInt();        for(int i=0;i<t;i++){            int a=s.nextInt();            int b=s.nextInt();            int c=s.nextInt();            int count=0;            if((a==0 && b==0) || (b==0 && c==0) || (a==0 && c==0)){                count=0;            }            else{                if(b>0 && c>0){                    int g=c/2;                    int min=Math.min(b,g);                    for(int j=0;j<min;j++){                        count=count+2;                        count=count+1;                    }                    b=b-min;                }                if(a>0 && b>0){                        int g1=b/2;                        int min1=Math.min(a,g1);                        for(int j1=0;j1<min1;j1++){                            count=count+2;                                                        count=count+1;                        }                        a=a-min1;                }            }                           System.out.println(count);        }    }    }   
