import java.util.*;public class Solution{    public static void main(String[] args)    {        Scanner cin=new Scanner(System.in);        int t=cin.nextInt();        while(t>0)        {            t--;            int a[]=new int[3];            for(int i=0;i<3;i++)a[i]=cin.nextInt();            int cnt1=0,cnt2=0,b=a[0],c=a[1],d=a[2];            while(b>=1&&c>=2)            {                b-=1;                c-=2;                cnt1+=3;            }            while(c>=1&&d>=2)            {                c-=1;                d-=2;                cnt1+=3;            }            while(a[1]>=1&&a[2]>=2)            {                a[1]-=1;                a[2]-=2;                cnt2+=3;            }            while(a[0]>=1&&a[1]>=2)            {                a[0]-=1;                a[1]-=2;                cnt2+=3;            }            System.out.println(Max(cnt1,cnt2));        }    }    static int Max(int a,int b)    {        if(a>=b)return a;        else return b;    }}
