import java.math.BigInteger;import java.util.*;public class _1238A {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int t = sc.nextInt();        while (t-- > 0) {            BigInteger x= new BigInteger(sc.next());            BigInteger y= new BigInteger(sc.next());            if((x.subtract(y)).equals(BigInteger.ONE))                System.out.println("NO");            else                System.out.println("YES");        }    }}
