// 124A - The number of positions// https://codeforces.com/problemset/problem/124/A import java.util.Scanner; public class CF124A {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int front = sc.nextInt();        int back = sc.nextInt();        int result = n - front - 1;        if (result > back) {            result = back + 1;        } else {            result = result + 1;        }        System.out.println(result);        sc.close();    }}
