import java.util.*;public class Solution{    public static void main(String[] args){        Scanner s=new Scanner(System.in);        int t=s.nextInt();        for(int i=0;i<t;i++){            int n=s.nextInt();            int a[]=new int[n];            for(int j=0;j<n;j++)                a[j]=s.nextInt();            int m=s.nextInt();            int b[]=new int[m];            for(int k=0;k<m;k++)                b[k]=s.nextInt();            long even=0,odd=0,even1=0,odd1=0;            for(int d=0;d<n;d++){                if(a[d]%2==0)                    even++;                else                    odd++;            }            for(int d1=0;d1<m;d1++){                if(b[d1]%2==0)                    even1++;                else                    odd1++;            }            long ans=even*even1+odd*odd1;            System.out.println(ans);            ans=0;                    }    }}
