import java.util.*;import java.util.Arrays;public class Main{    public static void main (String[] args)      {          Scanner sc=new Scanner(System.in);        int t=sc.nextInt();        for(int i=0;i<t;i++)        {            long n=sc.nextLong();            long p1=0,p2=0,q1=0,q2=0;            for(int j=0;j<n;j++)            {                long n1=sc.nextLong();                if(n1%2==0)                {                    p1++;                }                else                {                    q1++;                }            }            long m=sc.nextLong();            for(int j=0;j<m;j++)            {                long m1=sc.nextLong();                if(m1%2==0)                {                    p2++;                }                else                {                    q2++;                }            }            long total=((p1*p2)+(q1*q2));            System.out.println(total);        }    }  }         
