import java.util.Scanner;public class Ishu1{	public static void main(String[] args)	{	Scanner scan=new Scanner(System.in);	String str;	int nested=0,i,j;	int[] degree=new int[26];	char[] ch=new char[1000];	str=scan.next();	ch=str.toCharArray();	for(i=0;i<str.length();++i)		if(ch[i]>='a'&&ch[i]<='z')			{			if(ch[i-1]=='<')				{				for(j=1;j<=2*nested;++j)					System.out.print(" ");				System.out.println("<"+ch[i]+">");				++nested;				}			else if(ch[i-1]=='/')				{				--nested;				for(j=1;j<=2*nested;++j)					System.out.print(" ");				System.out.println("</"+ch[i]+">");				}			}	}}
