//import jdk.swing.interop.SwingInterOpUtils; import java.util.*;  public class Practise {     //public static int[][] dp;     public static void main(String[] args) {        //top- down approch        //fibonacci         Scanner sc = new Scanner(System.in); //right most a[i]>=q;//        long[]  dp = new long[100001];//        for(int i=0;i<=100000;i++){//            dp[i]=-1;//        }        int t = sc.nextInt();        while(t>0) {    int n =sc.nextInt();    int[] a=new int[n];    int[]  b =new int[n];    for(int  i=0;i<n;i++){a[i]=sc.nextInt();    }    for(int i=0;i<n;i++){        b[i]=sc.nextInt();    }    int ans=0;    int ad=0;     boolean tt=true;    Set<Integer> set =new HashSet<>();    List<Integer> list =new ArrayList<>();    for(int i=0;i<n;i++){        if(b[i]-a[i]<0){            tt=false;            break;        }else if(b[i]-a[i]>0){            set.add(b[i]-a[i]);            list.add(i);          }    }    if(set.size()>1)    {        tt=false;    }else {        for(int i=0;i<list.size()-1;i++){            if(list.get(i+1)-list.get(i)>1){                tt=false;break;            }         }    }    if(tt)        System.out.println("YES");    else        System.out.println("NO");             t--;        }     }     private static int Gcd(int gvd, int a) {        if(a==0)            return gvd;        else            return Gcd(a,gvd%a);    }      private static int CountLeft(int x, int[] arr) {        int l=-1;        int r =arr.length;        while(r>l+1){            int mid=l+(r-l)/2;            if(arr[mid]<=x){                l=mid;             }else                r=mid;         }        return  l;    }     private static int CountRight(int x ,int[] arr) {        int l=-1;        int r=arr.length;        while(r>l+1){            int mid=(l)+(r-l)/2;            if(arr[mid]>=x){                r=mid;            }else                l=mid;        }        return r;      }     private static boolean check(String toString) {        Stack<Character>  st=new Stack<>();        for(int  i=0;i<toString.length();i++){            if(toString.charAt(i)=='('){                st.push(toString.charAt(i));            }else if(st.isEmpty()){                return false;            }else            {                st.pop();            }        }        return st.isEmpty();    }  }    	 	 		 			 		  			 	      	
