import java.util.*;import java.util.Arrays;public class Main{    public static void main(String args[])    {        Scanner sc=new Scanner(System.in);        int t=sc.nextInt();        for(int i=0;i<t;i++)        {            int a=sc.nextInt();            int b=sc.nextInt();            int c=0;            if(a>b)            {                c=a-b;            }            else            {                c=b-a;            }            int count=0;            if(a==b)            {                System.out.println("0");            }            else            {               while(c!=0)               {                   if(c>=5)                   {                       count=count+(c/5);                       c=c%5;                                          }                   else if(c>=2 && c<5)                   {                       count=count+(c/2);                       c=c%2;                                          }                   else                   {                       count=count+(c/1);                       c=c%1;                                          }               }               System.out.println(count);            }                    }    }}
