import java.util.*;import java.io.*;public class Main {    public static void main(String[] args) throws IOException    {        Scanner in = new Scanner(new BufferedReader(new InputStreamReader(System.in)));        PrintWriter out = new PrintWriter(new OutputStreamWriter(System.out));                int t = in.nextInt();        while(t-- != 0)        {        	int a = in.nextInt();        	int b = in.nextInt();        	if(a > b)        	{        		int c = a;        		a = b;        		b = c;        	}        	int res = b - a;        	int ans = res / 10 * 2;        	res %= 10;        	int[] nums = new int[] {0, 1, 1, 2, 2, 1, 2, 2, 3, 3, 2};        	ans += nums[res];        	out.println(ans);        	        	        	        	        	        	        }                                in.close();        out.flush();            }    }		 	  		 	  				  		 	 	 	  	 	
