import java.util.Scanner;import java.util.HashMap;public class Main {    public static void main(String args[]) {        Scanner s = new Scanner(System.in);        int t = s.nextInt();        while(t-- != 0){            int n = s.nextInt();            String arr[] = new String[n];            HashMap<String,Integer> map = new HashMap<>();            for(int i = 0; i < n; i++){                arr[i] = s.next();                map.put(arr[i],map.getOrDefault(arr[i],0) + 1);            }            int count = 0;            for(int i = 0; i < n; i++){                if(map.get(arr[i]) > 1){                    char a[] = arr[i].toCharArray();                    boolean flag = false;                    for(int j = 9; j >= 0; j--){                        a[3] = (char)('0' + j);                        if(!map.containsKey(String.valueOf(a))){                            map.put(arr[i],map.get(arr[i]) - 1);                            arr[i] = String.valueOf(a);                            map.put(arr[i],1);                            flag = true;                            break;                        }                    }                    if(!flag){                        a = arr[i].toCharArray();                       for(int j = 9; j >= 0; j--){                          a[2] = (char)('0' + j);                          if(!map.containsKey(String.valueOf(a))){                            map.put(arr[i],map.get(arr[i]) - 1);                            arr[i] = String.valueOf(a);                            map.put(arr[i],1);                        }                        }                        }                    count++;                }                                                    }            System.out.println(count);            for(String str : arr){                System.out.println(str);            }                    }        }}
