import java.util.*;import java.util.Arrays;public class Main{    public static void main (String[] args)      {          Scanner sc=new Scanner(System.in);        int n=sc.nextInt();        for(int i=0;i<n;i++)        {            String a=sc.next();            a=a+"#";            char[] t=new char[a.length()];            for(int j=0;j<a.length();j++)            {                t[j]=a.charAt(j);            }            for(int j=0;j<a.length();j++)            {                if(t[j]=='?')                {                    if(j==0)                    {                        if(t[j+1]=='a')                        {                            t[j]='b';                        }                        else if(t[j+1]=='b')                        {                            t[j]='a';                        }                        else if(t[j+1]=='c')                        {                            t[j]='b';                        }                        else                        {                            t[j]='a';                        }                    }                    else                    {                        if(t[j-1]=='a')                        {                           if(t[j+1]=='a')                            {                                t[j]='b';                            }                            else if(t[j+1]=='b')                            {                                t[j]='c';                            }                            else if(t[j+1]=='c')                            {                                t[j]='b';                            }                            else                            {                                t[j]='b';                            }                        }                        if(t[j-1]=='b')                        {                           if(t[j+1]=='a')                            {                                t[j]='c';                            }                            else if(t[j+1]=='b')                            {                                t[j]='c';                            }                            else if(t[j+1]=='c')                            {                                t[j]='a';                            }                            else                            {                                t[j]='c';                            }                        }                        if(t[j-1]=='c')                        {                           if(t[j+1]=='a')                            {                                t[j]='b';                            }                            else if(t[j+1]=='b')                            {                                t[j]='a';                            }                            else if(t[j+1]=='c')                            {                                t[j]='b';                            }                            else                            {                                t[j]='b';                            }                        }                    }                }            }            int count=0;            for(int j=0;j<a.length()-1;j++)            {                if(t[j]==t[j+1])                {                    count=100;                    break;                }            }            if(count==100)            {                System.out.println("-1");            }            else            {                for(int j=0;j<a.length()-1;j++)                {                    System.out.print(t[j]);                }                System.out.println(" ");            }        }    }  } 
