//package com.codeforces.GlobalRound6; import java.io.*;import java.math.BigInteger;import java.util.StringTokenizer; public class Div2A {     static class FastReader {         BufferedReader br;        StringTokenizer st;         public FastReader() {            br = new BufferedReader(new InputStreamReader(System.in));        }         String next() {            while (st == null || !st.hasMoreElements()) {                try {                    st = new StringTokenizer(br.readLine());                }                catch (IOException e) {                    e.printStackTrace();                }            }            return st.nextToken();        }         int nextInt() {            return Integer.parseInt(next());        }         long nextLong() {            return Long.parseLong(next());        }         double nextDouble() {            return Double.parseDouble(next());        }         String nextLine() {            String str = "";            try {                str = br.readLine();            }            catch (IOException e) {                e.printStackTrace();            }            return str;        }    }     public static void main(String[] args) {         PrintWriter out = new PrintWriter(new BufferedOutputStream(System.out));         FastReader fsr = new FastReader();          String num;        int zeroes;        int curr;        int sum;         boolean found, skip;         int N = fsr.nextInt();          for (int i = 1; i <= N; i++) {             num = fsr.nextLine();             zeroes = sum = 0;             for (int j = 0; j < num.length(); j++) {                curr = num.charAt(j) - 48;                if (curr == 0) {                    zeroes++;                }                sum += (4 * curr);            }             found = false; skip = true;             for (int j = 0; j < num.length(); j++) {                curr = num.charAt(j) - 48;                if (curr == 0 && skip) {                    skip = false;                } else if ((sum - (3 * curr)) % 6 == 0) {                    found = true; break;                }            }             if (zeroes == num.length()) {                out.println("red");            } else if (zeroes >= 1 && found) {                out.println("red");            } else {                out.println("cyan");            }        }          out.flush();        out.close();    }}
