//package com.codeforces.GlobalRound6; import java.io.*;import java.math.BigInteger;import java.util.Arrays;import java.util.StringTokenizer; public class Div2B {     static class FastReader {         BufferedReader br;        StringTokenizer st;         public FastReader() {            br = new BufferedReader(new InputStreamReader(System.in));        }         String next() {            while (st == null || !st.hasMoreElements()) {                try {                    st = new StringTokenizer(br.readLine());                }                catch (IOException e) {                    e.printStackTrace();                }            }            return st.nextToken();        }         int nextInt() {            return Integer.parseInt(next());        }         long nextLong() {            return Long.parseLong(next());        }         double nextDouble() {            return Double.parseDouble(next());        }         String nextLine() {            String str = "";            try {                str = br.readLine();            }            catch (IOException e) {                e.printStackTrace();            }            return str;        }    }     public static void main(String[] args) {         PrintWriter out = new PrintWriter(new BufferedOutputStream(System.out));         FastReader fsr = new FastReader();          long x;        long div;        long rem;          int T = fsr.nextInt();          for (int i = 1; i <= T; i++) {             x = fsr.nextLong();             if (x < 15) {                out.println("NO");            } else {                div = (x / 14) - 1;                 rem = x - (14 * div);                 if (rem >= 15L && rem <= 20L) {                    out.println("YES");                } else {                    out.println("NO");                }            }        }          out.flush();        out.close();    }}
