import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;//import java.util.Scanner;import java.util.*; public class t{	static class FastReader	{		BufferedReader br;		StringTokenizer st;		public FastReader(){			br = new BufferedReader(new			InputStreamReader(System.in));		}		String next(){			while (st == null || !st.hasMoreElements()){				try{					st = new StringTokenizer(br.readLine());				}				catch (IOException e){					e.printStackTrace();				}			}			return st.nextToken();		}		int nextInt(){			return Integer.parseInt(next());		}		long nextLong(){			return Long.parseLong(next());		}		double nextDouble(){			return Double.parseDouble(next());		}		String nextLine()		{			String str = "";			try{				str = br.readLine();			}			catch (IOException e){				e.printStackTrace();			}			return str;		}	}  		public static void main(String[] args){		FastReader in=new FastReader();						int t  = in.nextInt();		while(t>0) {			t--;			long l = in.nextLong();			long rm = l/14;			long n = 0;			n=rm*14;			n++;			long lim = n+5;			if(l<=lim&&l>=n) {				if(l<15)					System.out.println("NO");				else				System.out.println("YES");			}else				System.out.println("NO");								}								}	}
