//package com.company;import java.io.*;import java.util.*; public class Main {     public static void main(String[] args) throws IOException {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        PrintWriter pw = new PrintWriter(System.out);        int t = Integer.parseInt(br.readLine());        while(t-->0) {            StringTokenizer st = new StringTokenizer(br.readLine());            int n = Integer.parseInt(st.nextToken()),k1 = Integer.parseInt(st.nextToken()),k2 = Integer.parseInt(st.nextToken());            boolean flag=false;            st = new StringTokenizer(br.readLine());            for(int i=0;i<k1;i++) {                if(Integer.parseInt(st.nextToken())==n) {                    flag=true;                    break;                }            }            st = new StringTokenizer(br.readLine());            pw.println(flag?"YES":"NO");        }        pw.flush();        pw.close();    }}
