import java.util.Scanner;import java.util.ArrayList;public class Main {    public static void main(String args[]) {        Scanner s = new Scanner(System.in);        int n = s.nextInt();        String str = s.next();        int b = 0,w = 0;        ArrayList<Integer> black = new ArrayList<>();        ArrayList<Integer> white = new ArrayList<>();        for(int i = 0; i < n; i++){            if(str.charAt(i) == 'B'){                b++;                black.add(i + 1);            }            else{                w++;                white.add(i + 1);            }        }        if(b % 2 == 1 && w % 2 == 1){            System.out.println(-1);            return;        }        ArrayList<Integer> ans = new ArrayList<>();        if(b % 2 == 1){            for(int i = 1; i < white.size(); i = i + 2){                for(int start = white.get(i) - 1; start >= white.get(i - 1); start--){                    ans.add(start);                }            }        }        else{            for(int i = 1; i < black.size(); i = i + 2){                for(int start = black.get(i) - 1; start >= black.get(i - 1); start--){                    ans.add(start);                }            }        }        System.out.println(ans.size());        for(int num : ans){            System.out.print(num + " ");        }    }}
