// template : secondThread import java.io.*;import java.util.*;import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;   public   class main2 {          public static void main(String[] args) {          try {                PrintWriter fop = new PrintWriter(System.out);              FastScanner fsca = new FastScanner();                 int n = fsca.nextInt();                String s = fsca.next();                char A[] = s.toCharArray() ;                char B[] = s.toCharArray() ;                int ct = 0 ;                StringBuilder sb = new StringBuilder() ;                // white conversion              for (int i = 0; i <n-1 ; i++) {                  if (A[i] != 'W'){                      A[i] = 'W' ;                      ct++ ;                      sb.append((i+1)+ " ") ;                      if (A[i+1] == 'W')                          A[i+1] = 'B' ;                      else A[i+1] = 'W' ;                  }              }              if (A[n-1] == 'W'){                  System.out.println(ct);                  if (ct != 0)                  System.out.println(sb);                  return;              }              ct = 0 ;              StringBuilder sb1 = new StringBuilder() ;               for (int i = 0; i <n-1 ; i++) {                  if (B[i] != 'B'){                      B[i] = 'B' ;                       ct++ ;                      sb1.append((i+1) + " ") ;                      if (B[i+1] == 'W')                          B[i+1] = 'B' ;                      else B[i+1] = 'W' ;                  }              }              if (B[n-1] == 'B'){                  System.out.println(ct);                  if (ct != 0)                  System.out.println(sb1);                  return;              }               System.out.println(-1);                                      fop.flush();              fop.close();          } catch (Exception e) {              return;          }        }      /*-----------------------------------------------------------------------------------------------------------------------------------------------*/        static void sieve(int n) {          boolean[] flag = new boolean[n];           for (int i = 2; i * i < n; i++) {              if (flag[i])                  continue;              else                  for (int j = i * i; j <= n; j += i) {                      flag[j] = true;                  }          }      }        static int gcd(int a, int b) {          if (b > a) {              int tenp = b;              b = a;              a = tenp;          }          int temp = 0;          while (b != 0) {              a %= b;              temp = b;              b = a;              a = temp;          }          return a;      }       static long gcd_long(long a, long b) {          if (b > a) {              long tenp = b;              b = a;              a = tenp;          }          long temp = 0;          while (b != 0) {              a %= b;              temp = b;              b = a;              a = temp;          }          return a;      }        static final Random random = new Random();       static void ruffleSort(int[] a) {          int n = a.length;//shuffle, then sort          for (int i = 0; i < n; i++) {              int oi = random.nextInt(n), temp = a[oi];              a[oi] = a[i];              a[i] = temp;          }          Arrays.sort(a);      }        static class FastScanner {          BufferedReader br = new BufferedReader(new InputStreamReader(System.in));          StringTokenizer st = new StringTokenizer("");           String next() {              while (!st.hasMoreTokens())                  try {                      st = new StringTokenizer(br.readLine());                  } catch (IOException e) {                      e.printStackTrace();                  }              return st.nextToken();          }           int nextInt() {              return Integer.parseInt(next());          }           int[] readArray(int n) {              int[] a = new int[n];              for (int i = 0; i < n; i++) a[i] = nextInt();              return a;          }           long[] readLongArray(int n) {              long[] a = new long[n];              for (int i = 0; i < n; i++)                  a[i] = nextLong();              return a;          }           long nextLong() {              return Long.parseLong(next());          }      }    }        
