import java.util.*;import java.math.*;public class MakeThemOdd{	public static void main(String[] args)		{			Scanner sc = new Scanner(System.in);			int runs = sc.nextInt();			while(runs-->0)			{				int size = sc.nextInt();				TreeSet<Integer> arr = new TreeSet<Integer>();				while(size-->0)				{					Integer num = sc.nextInt();					if(num%2==0)						arr.add(num);				}				long out = 0;				while(arr.size()>0)				{					Integer temp = arr.pollLast();					temp/=2;					if(temp%2==0)						arr.add(temp);					out++;				}				System.out.println(out);			}		}}
