import java.util.Scanner;public class Main {    public static void main(String args[]) {        Scanner s = new Scanner(System.in);        int t = s.nextInt();        while(t-- != 0){            long n = s.nextLong();            long k = s.nextLong();            long times = n/k;            long ans = times * k;            long rem = n - ans;            ans += Math.min(k/2,rem);            System.out.println(ans);        }    }}
