//package codechef;Â import java.util.*;import java.io.*;public class CodeChef{    public static int gcd(int a,int b)    {        if(b==0) return a;        return gcd(b,a%b);    }    public static int fact(int n)    {        int fact = 1;        for(int i = 1; i<=n; i++) fact*=i;        return fact;    }    public static boolean isPrime(int n)    {        if(n==2) return true;        else if(n%2==0) return false;        for(int i = 2; i<=Math.sqrt(n); i++)        {            if(n%i==0)            {                return false;            }        }        return true;    }    public static void main(String[] args)throws IOException    {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        String str[] = br.readLine().split(" ");            int n = Integer.parseInt(str[0]), m = Integer.parseInt(str[1]);            String s[] = br.readLine().split(" ");            String t[] = br.readLine().split(" ");            int q = Integer.parseInt(br.readLine());            while(q-->0)            {                int x = Integer.parseInt(br.readLine());                if(x%n!=0) System.out.print(s[x%n-1]);                else System.out.print(s[n-1]);                if(x%m!=0) System.out.println(t[x%m-1]);                else System.out.println(t[m-1]);            }    }}
