#Kadane's algodef max_sum(a):    maxnow = a[0]    maximum = a[0]    for i in range(1,len(a)):        maxnow += a[i]        if maxnow < a[i]:            maxnow = a[i]         maximum = max(maxnow, maximum)     return maximum for _ in range(int(input())):    n = int(input())    l = list(map(int,input().split()))    som  = sum(l)    r1 = max_sum(l[1:])    r2 = max_sum(l[0:len(l)-1])    r = [r1,r2]    ma = max_sum(l)    if som > ma:        print("YES")        continue     if som == ma:        if r.count(som) == 0:            print("YES")            continue     print("NO")
