import java.util.*;import java.util.Arrays;public class Main{    public static void main(String args[])    {        Scanner sc=new Scanner(System.in);        int n=sc.nextInt();        for(int j=0;j<n;j++)        {            int k=sc.nextInt();            int temp=0,res=0;            String students =sc.next();            char[] a=new char[k];            a=students.toCharArray();            for(int x=0;x<k;x++)            {                a[x]=students.charAt(x);            }                        for(int i=k-1;i>=0;i--)            {                if(a[i]=='A')                {                    res=Math.max(res,temp);                    temp=0;                }                else                {                    temp++;                }            }            System.out.println(res);        }                    }}
