    import java.util.*;    public class Solution{            public static void main(String[] args){        Scanner s=new Scanner(System.in);        int t=s.nextInt();        for(int i=0;i<t;i++){            int count=0,count1=0;            int n=s.nextInt();            s.nextLine();            String s1=s.nextLine();            boolean jt=false;            for(int j=0;j<s1.length();j++){                if(s1.charAt(j)=='A')                     jt=true;                if(s1.charAt(j)=='P' && jt==true){                    count++;                }                else{                    count=0;                }                if(count>count1)                   count1=count;                                }                            System.out.println(count1);                                                                        }      }      }    
