// I've had my share of sand kicked in my face but I've come through#pragma GCC optimize("Ofast")#define _CRT_SECURE_NO_WARNINGS#include "bits/stdc++.h" using namespace std; #define int long long#define pb push_back#define all(v) v.begin(),v.end()#define allr(v) v.rbegin(),v.rend()#define mod (int)(1e9+7) void solve() {    int n,m,k; cin>>n>>m>>k;    vector<int> a(n);    for(auto &it:a)        cin>>it;     if(k>=(m-1)){        int ans=*max_element(a.begin(),a.begin()+m);        ans=max(ans,*max_element(a.end()-m,a.end()));        cout<<ans<<'\n';        return;    }     int ans=0;     k=min(k,m-1);     for(int i=0;i<=k;i++){        int cur=1e18;        for(int j=0;j<(m-k);j++){            cur=min(cur,max(a[i+j],a[i+j+n-m]));        }        ans=max(ans,cur);    }    cout<<ans<<'\n';} int32_t main() {    #ifndef ONLINE_JUDGE    freopen("input.txt", "r", stdin);    freopen("output.txt", "w", stdout);    #endif    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t=1;    cin>>t;    while(t--){        solve();    }    return 0;}
