import java.util.*;public class file{public static void main(String[] args){Scanner scan=new Scanner(System.in);int test=scan.nextInt();for(int i=0;i<test;i++){int n=scan.nextInt();scan.nextLine();int c=0;String str=scan.nextLine();boolean flag=true;for(int j=0;j<str.length();j++){char ch= str.charAt(j);int t=Character.getNumericValue(ch);if(t%2!=0){c++;}if(c==2){System.out.println(str.substring(0,j+1));flag=false;break;}}if(flag){System.out.println("-1");}}}}
