import java.util.*;import java.io.*;public class A{	public static void main(String ar[])throws Exception	{		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));		int t = Integer.parseInt(br.readLine());		while(t-- > 0){			int k = Integer.parseInt(br.readLine());			String s = br.readLine();			StringBuffer sb = new StringBuffer(s);			int arr[] = new int[10];			for(int i = 0 ;i<s.length();i++)			{				arr[s.charAt(i)-'0']++;			}			int curr = 0 ;			for(int i = 0;i<10;i++)			{				curr += (arr[i]*i);			}						while(sb.length() > 0 && sb.charAt(sb.length() -1) % 2 == 0)			{				arr[sb.charAt(sb.length() -1) - '0']--;				curr -= (sb.charAt(sb.length() -1)- '0');				sb.deleteCharAt(sb.length() -1);			}			int i = 0;			while(curr % 2 != 0 && i<sb.length())			{				if(s.charAt(i) % 2 != 0)				{					sb.deleteCharAt(i);					break;				}				i++;			}			while(sb.length() > 0 && sb.charAt(sb.length() -1) % 2 == 0)			{				arr[sb.charAt(sb.length() -1) - '0']--;				curr -= (sb.charAt(sb.length() -1)- '0');				sb.deleteCharAt(sb.length() -1);			}			i = 0;			while(i<sb.length() && sb.charAt(i) == '0')			{				sb.deleteCharAt(i);			}			if(sb.length()  > 0){			System.out.println(sb);			}			else System.out.println("-1");														}			}}
