import java.util.Scanner;public class Main {    public static void main(String args[]) {        Scanner s = new Scanner(System.in);        int t = s.nextInt();        while(t-- != 0){            int n = s.nextInt();            String str = s.next();            String ans = "";            for(int i = 0; i < n; i++){                int k = str.charAt(i) - '0';                if(k % 2 == 1){                    ans = ans + str.charAt(i);                    if(ans.length() == 2){                        break;                    }                }            }            if(ans.length() == 2){                System.out.println(ans);            }            else{                System.out.println(-1);            }        }    }}
