import java.io.*;import java.util.*;public class arraysharpening {	public static void main(String[] args) throws IOException {		BufferedReader f = new BufferedReader(new InputStreamReader(System.in));		StringTokenizer st = new StringTokenizer(f.readLine());		int t = Integer.parseInt(st.nextToken());		while(t > 0) {			t --;			int n = Integer.parseInt(f.readLine());			int[] arr = new int[n];			st = new StringTokenizer(f.readLine());			for(int i = 0; i < n; i ++) {				arr[i] = Integer.parseInt(st.nextToken());			}			int front = 0;			int back = 0;			for(int i = 0; i < n; i++) {				if(arr[i] >= i) front = i;				else break;			}			for(int i = n - 1; i >= 0; i --) {				if(arr[i] >= (n - 1) - i) back = i;				else break;			}			if(front >= back) System.out.println("YES");			else System.out.println("NO");		}	}}
