import java.io.*;import java.util.*;public class A{    public static void main(String args[])    {        Scanner s=new Scanner(System.in);        int t=s.nextInt();        while(t-->0)        {            int a=s.nextInt();            int b=s.nextInt();            int c=s.nextInt();            int n=s.nextInt();            int sum=a+b+c+n;            if(sum%3==0)            {                int z=sum/3;                int p=Math.abs(z-a)+Math.abs(z-b)+Math.abs(z-c);                if(p==n)                {               System.out.println("Yes");                 }                else{                    System.out.println("No");                }            }            else{                System.out.println("No");            }        }    }}
