// package Quarantine; import javafx.util.Pair; import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.util.StringTokenizer; public class Aerodynamic {    public static void main(String[] args)throws IOException {        BufferedReader br=new BufferedReader(new InputStreamReader(System.in));        int n=Integer.parseInt(br.readLine());        int x[]=new int[n+1];        int y[]=new int[n+1];        StringTokenizer st;        for(int i=1;i<=n;i++){            st=new StringTokenizer(br.readLine());            int a=Integer.parseInt(st.nextToken());            int b=Integer.parseInt(st.nextToken());            x[i]=a;            y[i]=b;        }        if(n%2!=0){            System.out.println("NO");            return;        }        int j=n/2+1;        double midx=(x[1]+x[j])/2.0;        double midy=(y[1]+y[j])/2.0;        j++;        for(int i=2;i<=n/2;i++){            if((x[i]+x[j])/2.0!=midx){                System.out.println("NO");                return;            }            if((y[i]+y[j])/2.0!=midy){                System.out.println("NO");                return;            }            j++;        }        System.out.println("YES");    }}
