import sys# from collections import Counter# from itertools import permutations# import logging# logging.root.setLevel(level=logging.INFO) def gcd(bigger, smaller):    if smaller == 0:        return bigger    return gcd(smaller,bigger%smaller)   a = int(sys.stdin.readline().strip()) cases= a - 2 digits_sum = 0for base in range(2,a):    num = a    while num:        digits_sum += num % base        num //= baseg = gcd(digits_sum,cases) print(f"{digits_sum//g}/{cases//g}")
