//package Realization; import java.util.Scanner; public class ThreeStrings {     public void run(){        Scanner in = new Scanner(System.in);        int n = in.nextInt();         String a;        String b;        String c;         for (int i = 0; i < n; i++) {            a = in.next();            b = in.next();            c = in.next();            int as = 0;            for (int j = 0; j < a.length(); j++) {                if (a.charAt(j) == c.charAt(j) ||                        b.charAt(j) == c.charAt(j)) {                    as++;                } else {                    System.out.println("NO");                    break;                }                if (as == a.length())                    System.out.println("YES");            }         }     }      public static void main(String[] args) {        new ThreeStrings().run();    }}
