import java.util.*;public class Problem1307b {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int t = sc.nextInt();        while(t-->0) {            int n = sc.nextInt();            int x = sc.nextInt();            int[] a = new int[n];            int ans = Integer.MAX_VALUE;            for(int i = 0; i < n; i++) {                a[i] = sc.nextInt();                if(x%a[i]==0) {                    ans = Math.min(ans, x/a[i]);                }                else if(a[i] > x) {                    ans = Math.min(ans, x/a[i]+2);                }                else {                    ans = Math.min(ans, x/a[i]+1);                }            }            System.out.println(ans);        }    }}
