import java.util.Scanner;public class Word{    public static void main(String[] args){        Scanner s = new Scanner(System.in);        String ch = s.next();        String ch1="";        String ch2="";        boolean aux =true;        if(Character.isLowerCase(ch.charAt(0))){            for(int i=1;i<ch.length();i++){                if(Character.isLowerCase(ch.charAt(i))){                    aux = false;                    break;                }            }            if(aux==false){                System.out.println(ch);            }            else{                ch1=ch1 +Character.toUpperCase(ch.charAt(0));                for(int i=1;i<ch.length();i++){                    ch1= ch1 + Character.toLowerCase(ch.charAt(i));                }                System.out.println(ch1);            }        }        else{            for(int i=1;i<ch.length();i++){                if(Character.isLowerCase(ch.charAt(i))){                    aux = false;                    break;                }            }            if(aux==false){                System.out.println(ch);            }            else{                ch2=ch2 +Character.toLowerCase(ch.charAt(0));                for(int i=1;i<ch.length();i++){                    ch2= ch2 + Character.toLowerCase(ch.charAt(i));                }                System.out.println(ch2);            }        }    }}
