#include<cstdio>#include<cstring>#include<cstdlib>#include<algorithm>#include<cmath>#include<queue>#include<vector>#include<ctime>#include<map>#include<bitset>#include<set>#include<assert.h>#include<chrono>#include<random>#include<iostream>#define LL long long#define mp(x,y) make_pair(x,y)#define pll pair<long long,long long>#define pii pair<int,int>#define SZ(x) ((int)x.size())#define VI vector<int>using namespace std;mt19937 rnd(chrono::steady_clock::now().time_since_epoch().count());inline LL read(){	LL f=1,x=0;char ch=getchar();	while(ch<'0'||ch>'9'){if(ch=='-')f=-1;ch=getchar();}	while(ch>='0'&&ch<='9'){x=x*10+ch-'0';ch=getchar();}	return x*f;}int stack[20];template<typename T>inline void write(T x){	if(x<0){putchar('-');x=-x;}    if(!x){putchar('0');return;}    int top=0;    while(x)stack[++top]=x%10,x/=10;    while(top)putchar(stack[top--]+'0');}template<typename T>inline void pr1(T x){write(x);putchar(' ');}template<typename T>inline void pr2(T x){write(x);putchar('\n');}template<typename T>inline void chkmin(T &x,T y){x=x<y?x:y;}template<typename T>inline void chkmax(T &x,T y){x=x>y?x:y;}const int MAXN=20;const int MAXMASK=(1<<17);int n,K;bool hav[MAXMASK];int f[MAXN][MAXMASK][2][2],bin[25];int main(){	#ifdef Rose		double BeginJudgeTime=clock();	#endif	bin[0]=1;for(int i=1;i<=20;i++)bin[i]=bin[i-1]<<1;	n=read();K=read();for(int i=1;i<=K;i++)hav[read()-1]=1;	memset(f,-63,sizeof(f));int INF=f[0][0][0][0];	for(int i=0;i<bin[n];i+=2)	{		int u=hav[i],v=hav[i|1];		if(u+v==2)f[0][i][1][1]=1;		else if(u+v==1)f[0][i][0][1]=f[0][i][1][0]=1;		else f[0][i][0][0]=0;	}	for(int i=0;i<n-1;i++)		for(int j=0;j<bin[n];j+=bin[i+2])			for(int u=0;u<=1;u++)for(int v=0;v<=1;v++)if(f[i][j][u][v]!=INF)			for(int p=0;p<=1;p++)for(int q=0;q<=1;q++)if(f[i][j+bin[i+1]][p][q]!=INF)			{				int A=f[i][j][u][v],B=f[i][j+bin[i+1]][p][q];				chkmax(f[i+1][j][u][v],A+B+(u|p)+(v|q)+(p|v));				chkmax(f[i+1][j][u][q],A+B+(u|p)+(v|q)+(p|q));				chkmax(f[i+1][j][u][p],A+B+(u|p)+(v|q)+(p|v|q));				chkmax(f[i+1][j][p][v],A+B+(u|p)+(v|q)+(u|v));				chkmax(f[i+1][j][p][q],A+B+(u|p)+(v|q)+(u|q));				chkmax(f[i+1][j][p][u],A+B+(u|p)+(v|q)+(u|v|q));			}	int ans=0;	for(int u=0;u<=1;u++)for(int v=0;v<=1;v++)if(f[n-1][0][u][v]!=INF)		chkmax(ans,f[n-1][0][u][v]+(u|v));	pr2(ans);	#ifdef Rose		double EndJudgeTime=clock();		cerr<<"JudgeTime is"<<" ";		cerr<<(EndJudgeTime-BeginJudgeTime)/CLOCKS_PER_SEC<<endl;	#endif	return 0;}
