# Fast IO import sysinput = sys.stdin.readlinedef print(x, end='\n'):    sys.stdout.write(str(x) + end) # IO helpers def get_int():    return int(input())def get_list_ints():    return list(map(int, input().split()))def get_char_list():    s = input()    return list(s[:len(s) - 1])def get_tuple_ints():    return tuple(map(int, input().split()))def print_iterable(p):    print(" ".join(map(str, p))) def main():	for _ in range(get_int()):		a=get_int()		l=get_list_ints()		l.sort()		l.reverse()		for i in range(a):			if i!=a-1:				print(l[i],end=' ')			else:				print(l[i]) if __name__=='__main__':	main()
