//package com.codeforces.EducationalRound83; import java.util.Arrays;import java.util.Collections;import java.util.Scanner; public class Div2B {     public static void main(String[] args) {         Scanner scanner = new Scanner(System.in);         int t = Integer.parseInt(scanner.nextLine());         int n;         Integer[] arr;         for (int i = 0; i < t; i++) {             n = Integer.parseInt(scanner.nextLine());             arr = new Integer[n];             for (int j = 0; j < n; j++) {                arr[j] = scanner.nextInt();            }             scanner.nextLine();             Arrays.sort(arr, Collections.reverseOrder());             for (int j = 0; j < n; j++) {                System.out.printf("%d ", arr[j]);            }             System.out.println();        }         scanner.close();    }}
