import java.util.*;public class fastfoodrestaurant{    public static void main(String args[])    {        Scanner sc = new Scanner(System.in);        int t = sc.nextInt();        for(int i = 1; i<= t; i++)        {            int a = sc.nextInt();            int b = sc.nextInt();            int c = sc.nextInt();            int count =0;            int A[] = {1,0,0,1,1,0,1};            int B[] = {0,1,0,1,0,1,1};            int C[] = {0,0,1,0,1,1,1};            if(c>a)            {                int k=c;                c=a;                a=k;            }            for(int j = 0;j <=6;j++)            {                if(a-A[j]>=0&&b-B[j]>=0&&c-C[j]>=0)                {                    a=a- A[j];                    b=b- B[j];                    c=c -C[j];                    count++;                }            }            System.out.println(count);        }    }}
